module DifficultyFraction (difficultyUnits, difficulty) where

import Text.Printf (printf)
import Test.Tasty (TestTree, testGroup)
import Test.Tasty.HUnit as HU ((@?=), testCase)

import qualified "flight-gap-effort" Flight.Score as FS (gradeDifficulty)
import "flight-gap-allot" Flight.Score
    ( Pilot(..)
    , PilotName(..)
    , PilotId(..)
    , isNormal
    )
import "flight-gap-effort" Flight.Score
    ( DifficultyFraction(..)
    , ChunkDifficultyFraction(..)
    , Difficulty(..)
    )

import TestNewtypes
import Km

nullPilot :: Pilot
nullPilot = Pilot (PilotId "", PilotName "")

nullPilots :: [Pilot]
nullPilots = repeat nullPilot

difficulty :: DfTest -> Bool
difficulty (DfTest (dBest, xs)) =
    all isNormal $ f <$> diffFracs (FS.gradeDifficulty dBest nullPilots xs)
    where
        f (DifficultyFraction df) = realToFrac df :: Double

diffFracs :: (a, Difficulty) -> [DifficultyFraction]
diffFracs = fmap frac . fractional . snd

dfUnit :: [Double] -> [Double] -> _
dfUnit xs dfs =
    let ds = distKms xs
        dfs' = DifficultyFraction . realToFrac <$> dfs
    in
        HU.testCase
            (printf "%5d pilots @ %s km => %s" (length ds) (showKms ds) (showDfs dfs))
            ((show . diffFracs) (FS.gradeDifficulty (bd ds) nullPilots ds) @?= show dfs')

showDfs :: [Double] -> String
showDfs xs
    | 1 <- length xs
    , y : _ <- xs = printf "[%7.4f]" y

    | 2 <- length xs
    , i : j : _ <- xs = printf "[%6.4f, %7.4f]" i j

    | length xs > 2
    , i : j : _ <- xs
    , k : _ <- reverse xs = printf "[%6.4f, %6.4f, ... %7.4f]" i j k

    | otherwise = show xs

